import React, { Fragment } from "react";
import {
  TodoListSection,
  TodoHeader,
  ListTitle,
  OtherBtn,
  ListUl,
  Radio,
  TodoLi,
  TodoText,
  DeleteBtn,
} from "../Style";

export const TodoList = ({ todo, isOn, setTodo }) => {
  const allCheck = (onTag) => {
    if (onTag !== "모두") return true;
    else return false;
  };
  const deleteTodo = (e) => {
    let data = todo.filter((arr) => arr.cate === isOn);
    const removeData = data[0].todo.filter((todo) => todo !== e.target.alt);
    let deleteData = (data[0] = {
      id: data[0].id,
      cate: data[0].cate,
      todo: [...removeData],
    });
    let deleteTodo = todo.map((arr) =>
      arr.cate === isOn ? (arr = deleteData) : arr
    );
    setTodo(deleteTodo);
  };

  return (
    <>
      {allCheck(isOn) ? (
        isOn !== "" ? (
          <Fragment>
            <TodoListSection>
              <TodoHeader>
                <ListTitle>{isOn}</ListTitle>
                <OtherBtn src="img/Other_btn.svg" />
              </TodoHeader>
              <ListUl>
                {todo.map((data) => {
                  return data.cate === isOn
                    ? data.todo.map((todo, index) => {
                        return (
                          <TodoLi key={index}>
                            <Radio type={"checkbox"} id={index} />
                            <label id={index} />
                            <TodoText>{todo}</TodoText>
                            <DeleteBtn
                              src="img/close_btn.svg"
                              alt={todo}
                              onClick={(e) => deleteTodo(e)}
                            />
                          </TodoLi>
                        );
                      })
                    : null;
                })}
              </ListUl>
            </TodoListSection>
          </Fragment>
        ) : null
      ) : (
        todo.map((data) => {
          return (
            <Fragment>
              <TodoListSection>
                <TodoHeader>
                  <ListTitle>{data.cate}</ListTitle>
                  <OtherBtn src="img/Other_btn.svg" />
                </TodoHeader>{" "}
              </TodoListSection>
            </Fragment>
          );
        })
      )}
    </>
  );
};
