import React, { useState } from "react";
import {
  HeaderContainer,
  Title1,
  AddBtn,
  AddBtnImg,
  SubText,
  PlusLink,
} from "../Style/ComponentStyle/HeaderStyle";

import { Link } from "react-router-dom";

export default function Header({ Title, Subtext }) {
  const [visible, setVisible] = useState(true);
  return (
    <>
      <HeaderContainer>
        <Title1>{Title}</Title1>
        <Link
          to="/add"
          onClick={() => {
            setVisible(!visible);
          }}
        >
          <AddBtnImg
            className={visible ? "removeBtn" : null}
            src="img/add_btn.svg"
            alt="게시글 추가 버튼"
          />
        </Link>
      </HeaderContainer>
      <SubText>{Subtext}</SubText>
    </>
  );
}
