import { createGlobalStyle } from "styled-components";
export const GlobalStyle = createGlobalStyle`
*, *::before,*::after{
    box-sizing: border-box;
    border: none;
    list-style: none;
    padding: 0;
    margin: 0;
    font-size: 16px;
}
html {
    background-color: #f0f0f0;
}
body {
    background-color: #ffffff;
    font-family: 'Pretendard';
    overflow: hidden; 
    position: static;
    left: 0; 
    top: 0; 
    width: 50%;
    margin: 4rem auto;
    border-radius: 1rem;
}
`;
