import styled from "styled-components";

export const TagsInput = styled.div`
  font-size: 0.88rem;
  display: flex;
  align-items: flex-start;
  flex-wrap: wrap;
  width: 100%;
  padding: 0.63em 1.5em 1.25em 1.25em;
  > ul {
    display: flex;
    flex-wrap: wrap;
    margin: 0.5rem 0 0 0;
    > .tag {
      width: auto;
      height: 2rem;
      display: flex;
      align-items: center;
      justify-content: center;
      color: #bdd0d9;
      padding: 0 0.5rem;
      border: 1px solid #d8e5ea;
      border-radius: 2em;
      margin-right: 0.63em;
      > .tag-close-icon {
        display: block;
        width: 16px;
        height: 16px;
        line-height: 16px;
        text-align: center;
        font-size: 14px;
        margin-left: 8px;
        color: #333;
        border-radius: 50%;
        background: #fff;
        cursor: pointer;
      }
    }
  }
  > input {
    flex: 1;
    border: none;
    height: 46px;
    font-size: 14px;
    padding: 4px 0 0 0;
    :focus {
      outline: transparent;
    }
  }
  &:focus-within {
    border: 1px solid var(--coz-purple-600);
  }
`;
