import { createGlobalStyle } from "styled-components";
export const GlobalStyle = createGlobalStyle`
*, *::before,*::after{
    box-sizing: border-box;
    border: none;
    list-style: none;
    padding: 0;
    margin: 0;
}
body {
    font-family: 'Pretendard';
    overflow: hidden; 
    position: fixed; 
    left: 0; top: 0; 
    width: 100%; 
    height: 100%;
}
.App {
    min-width: 320px;
}

`;
