  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 2.5em 1.5em 1.25em 1.25em;
`;
export const Title1 = styled.h1`
  font-style: normal;
  font-weight: 800;
  font-size: 32px;
  line-height: 32px;
  letter-spacing: -1px;
  color: #3f778c; ;
`;
export const SubText = styled.p`
  font-style: normal;
  font-weight: 400;
  line-height: 1.5em;
  color: #b1b1b1;
  font-size: 1em;
  padding: 0 1.25em 0 1.25em;
`;
export const PlusLink = styled(Link)``;
export const AddBtn = styled.button`
  background: white;
  border: none;
  &:hover {
    border: 1px;
    border-color: #111;
  }
  &.RemoveBtn {
    visibility: hidden;
  }
`;
export const AddBtnImg = styled.img`
  width: 30px;
  cursor: pointer;
`;
