import React, { Fragment } from "react";
import {
  TodoListSection,
  TodoHeader,
  ListTitle,
  OtherBtn,
  ListUl,
  Radio,
  TodoLi,
  TodoText,
  DeleteBtn,
} from "../Style";

export const TodoList = ({ todo, isOn, setTodo }) => {
  const deleteTodo = (e) => {
    let data = todo.filter((arr) => arr.cate === isOn);
    const removeData = todo.map((data) =>
      data.cate === isOn
        ? data.todo.filter((todo) => {
            return todo !== e.target.alt;
          })
        : null
    );
    let deleteData = (data[0] = {
      id: data[0].id,
      cate: data[0].cate,
      todo: [...removeData],
    });
    let deleteTodo = todo.map((arr) =>
      arr.cate === isOn ? (arr = deleteData) : arr
    );
    console.log(deleteTodo);
    // setTodo(deleteTodo);
  };
  return (
    <>
      {isOn !== "" ? (
        <Fragment>
          <TodoListSection>
            <TodoHeader>
              <ListTitle>{isOn}</ListTitle>
              <OtherBtn src="img/Other_btn.svg" />
            </TodoHeader>
            <ListUl>
              {todo.map((data) => {
                return data.cate === isOn
                  ? data.todo.map((todo, index) => {
                      return (
                        <TodoLi key={index}>
                          <Radio type={"radio"} />
                          <TodoText>{todo}</TodoText>
                          <DeleteBtn
                            src="img/close_btn.svg"
                            alt={todo}
                            onClick={(e) => deleteTodo(e)}
                          />
                        </TodoLi>
                      );
                    })
                  : null;
              })}
            </ListUl>
          </TodoListSection>
        </Fragment>
      ) : null}
    </>
  );
};
