import styled from "styled-components";

export const TagsInput = styled.div`
  display: flex;
  align-items: flex-start;
  flex-wrap: wrap;
  width: 100%;
  padding: 0.63em 1.5em 1.25em 1.25em;
  > .title {
    margin-top: 1.2em;
  }
  > ul {
    display: flex;
    flex-wrap: wrap;
    margin: 0.5rem 0 0 0;
    > .tag {
      width: auto;
      height: 2rem;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 0.88rem;
      color: #bdd0d9;
      padding: 0 0.5rem;
      border: 1px solid #d8e5ea;
      border-radius: 2em;
      margin-right: 0.63em;
      margin-bottom: 0.63em;

      > .tag-close-icon {
        display: block;
        width: 16px;
        height: 16px;
        line-height: 16px;
        text-align: center;
        margin-left: 8px;
        color: #bdd0d9;
        border-radius: 50%;
        background: #fff;
        cursor: pointer;
      }
    }
  }
  > input {
    flex: 1;
    border: none;
    width: 100%;
    color: #ced7e0;
    background-color: #f9fafb;
    height: 46px;
    padding-left: 1.2em;
    margin-top: 0.88rem;
    border-radius: 30em;
    ::placeholder {
      color: #ced7e0;
    }
    :focus {
      outline: transparent;
    }
  }
  &:focus-within {
    border: 1px solid var(--coz-purple-600);
  }
`;
