import React from "react";
import styled from "styled-components";

export const HeaderContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 2.5em 1.5em 1.25em 1.25em;
`;
export const Title1 = styled.h1`
  font-style: normal;
  font-weight: 800;
  font-size: 32px;
  line-height: 32px;
  letter-spacing: -1px;
  color: #3f778c; ;
`;
export const SubText = styled.p`
  font-style: normal;
  font-weight: 400;
  line-height: 1.5em;
  color: #999999;
  padding: 0 1.25em 0 1.25em;
`;
export const AddBtn = styled.button`
  background: white;
  border: none;
  &:hover {
    border: 1px;
    border-color: #111;
  }
`;
export const AddBtnImg = styled.img`
  width: 30px;
  cursor: pointer;
`;
//작업 추가 버튼 구현

export default function Header() {
  return (
    <>
      <HeaderContainer>
        <Title1>오늘의 할 일</Title1>
        <AddBtn>
          <AddBtnImg src="img/add_btn.svg" alt="게시글 추가 버튼" />
        </AddBtn>
      </HeaderContainer>
      <SubText>자정이 지나면 달성한 할 일들이 초기화 돼요!</SubText>
    </>
  );
}
