import React from "react";
import Header from "../Components/Header";
import { MainTag } from "../Components/Tag";
import { TodoList } from "../Components/TodoList";

export const TodoLists = ({ tags, setTags }) => {
  return (
    <>
      <Header
        Title={"오늘의 할 일"}
        Subtext={"자정이 지나면 달성한 할 일들이 초기화 돼요!"}
      ></Header>
      <ul id="tags">
        {tags.map((tag, index) => (
          <li
            onClick={handleTag}
            key={index}
            className={`tag ${isOn ? "one-tag" : ""}`}
          >
            <span className="tag-title">{tag}</span>
            <span onClick={() => removeTags(index)} className="tag-close-icon">
              X
            </span>
          </li>
        ))}
      </ul>
      <TodoList></TodoList>
    </>
  );
};
