//태그기능 구현
import { useState } from "react";
import { TagsInput } from "../Style/ComponentStyle/TagStyle";
import { ListTitle } from "../Style/ComponentStyle/TodoStyle";

export const SetTag = ({ tags, setTags, tagState }) => {
  const [isOn, setIsOn] = useState(false);

  const handleTag = (e) => {
    setIsOn(!isOn);
  };

  const removeTags = (indexToRemove) => {
    setTags(tags.filter((element, index) => index !== indexToRemove));
  };

  const addTags = (event) => {
    if (
      event.key === "Enter" &&
      event.target.value !== "" &&
      !tags.includes(event.target.value)
    ) {
      setTags([...tags, event.target.value]);
      event.target.value = "";
    }
  };

  return (
    <>
      <TagsInput>
        <ListTitle className="title">태그</ListTitle>
        <ul id="tags">
          {tags.map((tag, index) => (
            <li
              onClick={handleTag}
              key={index}
              className={`tag ${isOn ? "one-tag" : ""}`}
            >
              <span className="tag-title">{tag}</span>
              <span
                onClick={() => removeTags(index)}
                className="tag-close-icon"
              >
                X
              </span>
            </li>
          ))}
        </ul>
        <input
          className="tag-input"
          type="text"
          onKeyUp={(e) => {
            addTags(e);
          }}
          placeholder="태그를 입력해 주세요."
        />
      </TagsInput>
    </>
  );
};
