    <Fragment>
      <TodoLi>
        <Radio type={"radio"} />
        <TodoText>adsfasdf</TodoText>
        <DeleteBtn
          src="img/close_btn.svg"
          alt="닫기 버튼"
          onClick={(e) => handleRemove(e)}
        ></DeleteBtn>
      </TodoLi>
    </Fragment>
  );
};

export const TodoList = ({ tags, todo }) => {
  return (
    <Fragment>
      {tags.map((e, i) => (
        <TodoListSection>
          <TodoHeader>
            <ListTitle>{e}</ListTitle>
            <OtherBtn src="img/Other_btn.svg" />
          </TodoHeader>
          <ListUl>
            {todo.map((text) => {
              todo.indexOf(text) === i ? (
                <Todo key={i} text={text} />
              ) : (
                <Todo key={i} text={text}></Todo>
              );
            })}
          </ListUl>
        </TodoListSection>
      ))}
    </Fragment>
  );
};

